require_once('workflows.php');
require_once('FileCache.php');

function load_data($api,$query) {

    $opts = array(  
        'http'=>array(  
        'method'=>"GET",  
        'timeout'=>10
        )
    );  
    $context = stream_context_create($opts);       
    $obj = false;


    $fck  = md5($query);
    $data = FileCache::get($fck);
        if(!$data)
        {
            $xml = @file_get_contents( $api.$query,false, $context);
            $data = $xml;            
            FileCache::set($fck,$data,86400);
        }
    $obj=json_decode($data);

    return $obj;
}

$wf = new Workflows();

$orig = "{query}";


date_default_timezone_set('Asia/Shanghai');
if($orig){
    $obj = load_data('http://api.staticfile.org/v1/search?q=',$orig);
    $int = 1;
    if($obj != null):
        foreach( $obj->libs as $key => $item ):
            if(!stristr($item -> name, $orig) && !stristr($item -> description, $orig)) continue;

            $name = $item -> name;
            $filename = $item -> filename;
            $homepage = $item -> homepage;
            $version = $item -> version;
            $description = $item -> description;
            $workflow_type = $item -> workflow_type;
            $assets = $item -> assets;
            $repositories = $item -> repositories;
            $keywords = $item -> keywords;

            $wf->result( $name, 'http://cdn.staticfile.org/'.$name.'/'.$version.'/'.$filename, '[v'.$version.'] '.$name,  $description . ' — @'.$homepage, 'default.png'  );
            $int++; 
        endforeach;
    endif;
}

$results = $wf->results();
if ( count( $results ) == 0 ):
    $wf->result( 'CDNworkflow_500', 'Please make sure your internet works well.', 'No responce, try again!', 'Please make sure your internet works well.', 'icon.png' );
endif;

echo $wf->toxml();

